                    Cancel
                  </button>
                  <button
                    type="submit"
                    className="flex-1 py-3 bg-blue-500 text-white rounded-lg font-medium hover:bg-blue-600 hover:scale-105 cursor-pointer"
                  >
                    Save Address
                  </button>
                </div>
              </div>
            </form>
          </div>
        )}

        {/* Continue Button - Only show if we have addresses and not showing the form */}
        {addresses.length > 0 && !showNewAddressForm && (
          <div className="fixed bottom-0 left-0 right-0 bg-white border-t border-gray-200 p-4">
            <button
              onClick={handleContinue}
              disabled={!selectedAddressId}
              className={`w-full py-4 rounded-lg text-white font-medium transition-colors cursor-pointer hover:scale-105 ${
                selectedAddressId ? 'bg-blue-500 hover:bg-blue-600' : 'bg-gray-300 cursor-not-allowed'
              }`}
            >
              Continue
            </button>
          </div>
        )}
      </div>
    </div>
  );
}",
  "src/app/booking/datetime/page.tsx": "'use client';

import { useState, useEffect } from 'react';
import { useRouter } from 'next/navigation';
import { useAuth } from '@/contexts/AuthContext';
import { bookingAPI } from '@/services/api';
import LoadingOverlay from '@/components/ui/LoadingOverlay';
import Button from '@/components/ui/Button';
import Card from '@/components/ui/Card';
import { ArrowLeft, Clock, Calendar, Check, Info, DollarSign } from 'lucide-react';
import Link from 'next/link';
import Cookies from 'js-cookie';

export default function DateTimeSelectionPage() {
  const [selectedDates, setSelectedDates] = useState<string[]>([]);
  const [selectedTimeSlots, setSelectedTimeSlots] = useState<string[]>([]);
  const [availableDates, setAvailableDates] = useState<{ date: Date; formatted: string; day: string; dayNum: string; month: string }[]>([]);
  const [availableTimeSlots, setAvailableTimeSlots] = useState<{ id: string; time: string; isSelected: boolean }[]>([]);
  const [loading, setLoading] = useState(true);
  const [cleaningTime, setCleaningTime] = useState("2 hours 36 min");
  const [cleaningDuration, setCleaningDuration] = useState(156); // in minutes
  const [bookingMode, setBookingMode] = useState<'specific' | 'flexible'>('flexible');
  const [multipleCleanersRequired, setMultipleCleanersRequired] = useState(false);
  const { isAuthenticated, isLoading } = useAuth();
  const router = useRouter();

  useEffect(() => {
    if (!isLoading && !isAuthenticated) {
      router.push('/login');
      return;
    }

    // Check if we have booking service and address data
    const bookingService = Cookies.get('bookingService');
    const bookingAddress = Cookies.get('bookingAddress');
    if (!bookingService || !bookingAddress) {
      router.push('/booking/new');
      return;
    }

    // Generate next 7 days as available dates
    generateDates();
    generateTimeSlots();

    // Check if multiple cleaners are needed
    setMultipleCleanersRequired(cleaningDuration > 360); // 6 hours in minutes

    setLoading(false);
  }, [isAuthenticated, isLoading, router, cleaningDuration]);

  // Generate dates for the next 7 days starting from tomorrow
  const generateDates = () => {
    const dates = [];
    const today = new Date();
    const daysOfWeek = ['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'];
    const months = ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec'];

    // Start from tomorrow for 7 days
    for (let i = 1; i <= 7; i++) {
      const date = new Date();
      date.setDate(today.getDate() + i);

      dates.push({
        date: date,
        formatted: date.toISOString().split('T')[0],
        day: daysOfWeek[date.getDay()],
        dayNum: date.getDate().toString(),
        month: months[date.getMonth()]
      });
    }

    setAvailableDates(dates);
  };

  // Generate time slots based on the cleaning duration and business rules
  const generateTimeSlots = () => {
    const slots = [];
    const startHour = 8; // 8 AM
    const endHour = 20; // 8 PM (cutoff time)
    const maxSingleCleanerDuration = 360; // 6 hours in minutes

    // Calculate the latest possible start time
    const durationHours = cleaningDuration / 60;
    const latestStartHour = endHour - durationHours;

    // Generate slots from 8 AM until latest possible start time
    for (let hour = startHour; hour <= latestStartHour; hour += 2) {
      // Format the start time
      const startHourFormatted = hour > 12 ? hour - 12 : hour;
      const startAmPm = hour >= 12 ? 'PM' : 'AM';

      // Calculate and format the end time
      const endHour = hour + 2;
      const endHourFormatted = endHour > 12 ? endHour - 12 : endHour;
      const endAmPm = endHour >= 12 ? 'PM' : 'AM';

      // Create the time slot string
      const timeSlot = `${startHourFormatted}:00 ${startAmPm} - ${endHourFormatted}:00 ${endAmPm}`;

      slots.push({
        id: hour.toString(),
        time: timeSlot,
        isSelected: false
      });
    }

    setAvailableTimeSlots(slots);
  };

  // Handle date selection logic
  const handleDateSelection = (formatted: string) => {
    if (bookingMode === 'specific' && selectedDates.length === 1 && !selectedDates.includes(formatted)) {
      // In specific mode, only one date can be selected
      setSelectedDates([formatted]);
      return;
    }

    const updatedSelection = [...selectedDates];
    const index = updatedSelection.indexOf(formatted);

    if (index > -1) {
      // Deselect the date
      updatedSelection.splice(index, 1);
    } else {
      // In flexible mode, allow up to 3 selections
      if (updatedSelection.length < 3) {
        updatedSelection.push(formatted);
      }
    }

    setSelectedDates(updatedSelection);
  };

  // Handle time slot selection logic
  const handleTimeSlotSelection = (id: string) => {
    const updatedTimeSlots = [...availableTimeSlots].map(slot => {
      if (slot.id === id) {
        return {...slot, isSelected: !slot.isSelected};
      }

      // In specific mode, only allow one selection
      if (bookingMode === 'specific' && !slot.isSelected) {
        return slot;
      }

      return slot;
    });

    // Count selected time slots
    const selectedSlots = updatedTimeSlots.filter(slot => slot.isSelected).map(slot => slot.id);

    // In specific mode, ensure only one time slot is selected
    if (bookingMode === 'specific' && selectedSlots.length > 1) {
      const clickedSlot = updatedTimeSlots.find(slot => slot.id === id);
      updatedTimeSlots.forEach(slot => {
        slot.isSelected = slot.id === id ? clickedSlot!.isSelected : false;
      });
    }

    // In flexible mode, ensure maximum 3 selections
    if (bookingMode === 'flexible' && selectedSlots.length > 3) {
      return;
    }

    setAvailableTimeSlots(updatedTimeSlots);
    setSelectedTimeSlots(updatedTimeSlots.filter(slot => slot.isSelected).map(slot => slot.id));
  };

  // Toggle between specific and flexible booking modes
  const toggleBookingMode = (mode: 'specific' | 'flexible') => {
    if (mode === bookingMode) return;

    setBookingMode(mode);

    // Reset selections when changing modes
    setSelectedDates([]);
    setAvailableTimeSlots(availableTimeSlots.map(slot => ({...slot, isSelected: false})));
    setSelectedTimeSlots([]);
  };

  // Check if the discount should be applied (3 time slots selected in flexible mode)
  const shouldApplyDiscount = () => {
    return bookingMode === 'flexible' && selectedTimeSlots.length === 3;
  };

  // Handle the continue button click
  const handleContinue = () => {
    console.log("handleContinue called");
    // Validate selections - simply check that at least one date and time are selected
    if (selectedDates.length === 0 || selectedTimeSlots.length === 0) {
      console.log("Validation failed - missing dates or time slots");
      return;
    }

    console.log("Validation passed, saving to cookies");

    // Create the data object
    const dateTimeData = {
      preferredDates: selectedDates,
      preferredTimeSlots: selectedTimeSlots.map(id => availableTimeSlots.find(slot => slot.id === id)?.time),
      flexibleScheduling: bookingMode === 'flexible',
      discountApplied: shouldApplyDiscount()
    };

    // Store date/time in cookies
    Cookies.set('bookingDateTime', JSON.stringify(dateTimeData), { expires: 7 });

    console.log("About to navigate to summary page");

    setTimeout(() => {
      router.push('/booking/summary');
    }, 100);
  };

  // Check if the continue button should be enabled
  const isContinueEnabled = () => {
    // Enable button as soon as at least one date and one time are selected
    return selectedDates.length > 0 && selectedTimeSlots.length > 0;
  };

  if (isLoading || loading) {
    return <LoadingOverlay visible={true} message="Loading scheduling options..." />;
  }

  return (
    <div className="min-h-screen bg-white px-4 py-0">
      <div className="max-w-md mx-auto">
        <div className="py-4 flex items-center border-b border-gray-200">
          <Link href="/booking/address" className="mr-2">
            <ArrowLeft size={20} className="text-gray-800" />
          </Link>
          <h1 className="text-lg font-semibold">Select Date & Time</h1>
        </div>

        <div className="py-4">
          <h2 className="text-2xl font-bold text-gray-800 mb-1">When do you need cleaning?</h2>
          <p className="text-gray-500 mb-6">
            Select your preferred dates and times for your cleaning service
          </p>

          {/* Max Clean Time Section */}
          <div className="bg-blue-50 rounded-lg p-4 mb-6">
            <div className="flex items-start mb-1">
              <Clock size={20} className="text-blue-500 mr-2 mt-1" />
              <div>
                <h3 className="font-semibold text-gray-800">Max Clean Time</h3>
                <p className="text-blue-500 text-xl font-bold">{cleaningTime}</p>
              </div>
            </div>
            <p className="text-gray-600 text-sm">
              Based on your property size and selected services, we've calculated the maximum time needed for your cleaning.
            </p>
            <p className="text-gray-500 text-sm italic mt-2">
              Note: All cleanings must finish by 6:00 PM, so your available time slots are limited accordingly.
            </p>

            {multipleCleanersRequired && (
              <div className="mt-2 p-2 bg-yellow-50 border border-yellow-200 rounded-md text-sm text-yellow-700">
                <Info size={16} className="inline-block mr-1" />
                This cleaning will require multiple cleaners due to its duration.
              </div>
            )}
          </div>

          {/* Flexible Scheduling Section */}
          <div className="bg-blue-50 rounded-lg p-4 mb-6">
            <div className="flex items-center justify-between mb-3">
              <div className="flex items-start">
                <Info size={20} className="text-blue-500 mr-2 mt-1" />
                <div>
                  <h3 className="font-semibold text-gray-800">Flexible Scheduling</h3>
                  <p className="text-gray-600 text-sm">
                    Select multiple dates and times if you're flexible. You'll receive a 5% discount for flexible scheduling!
                  </p>
                </div>
              </div>
            </div>

            <div className="flex items-center space-x-4 mb-2">
              <div 
                className={`px-4 py-2 rounded-full cursor-pointer hover:scale-105 transition-colors ${
                  bookingMode === 'specific' 
                    ? 'bg-blue-500 text-white' 
                    : 'bg-gray-200 text-gray-700'
                }`}
                onClick={() => toggleBookingMode('specific')}
              >
                Specific Time
              </div>
              <div 
                className={`px-4 py-2 rounded-full cursor-pointer hover:scale-105 transition-colors ${
                  bookingMode === 'flexible' 
                    ? 'bg-blue-500 text-white' 
                    : 'bg-gray-200 text-gray-700'
                }`}
                onClick={() => toggleBookingMode('flexible')}
              >
                Flexible Time
              </div>
            </div>

            {shouldApplyDiscount() && (
              <div className="bg-green-400 text-white text-sm font-medium rounded-full px-4 py-2 inline-flex items-center">
                <DollarSign size={16} className="mr-1" /> 5% Discount Applied
              </div>
            )}
          </div>

          {/* Date Selection Section */}
          <div className="mb-6">
            <div className="flex items-center justify-between mb-3">
              <div className="flex items-center">
                <Calendar size={18} className="text-blue-500 mr-2" />
                <h3 className="font-semibold text-gray-800">Select Date(s)</h3>
              </div>
              <span className="text-blue-500 text-sm">
                {selectedDates.length}/{bookingMode === 'flexible' ? '3' : '1'} selected
              </span>
            </div>

            <div className="grid grid-cols-4 gap-2">
              {availableDates.map((dateObj) => (
                <div
