                  value={specialInstructions}
                  onChange={handleSpecialInstructionsChange}
                />
              </div>

          {/* Continue Button */}
          <button
            className="w-full py-4 bg-blue-500 text-white rounded-lg text-center font-medium my-8 transition-colors hover:bg-blue-600 hover:scale-105 cursor-pointer"
            onClick={handleContinue}
          >
            Continue
          </button>
        </div>
      </div>
      {/* Membership Popup */}
      <MembershipPopup 
        isOpen={showMembershipPopup}
        onClose={() => setShowMembershipPopup(false)}
        frequency={membershipFrequency}
      />
      <CleaningSuppliesModal
        isOpen={showSuppliesModal}
        onClose={() => setShowSuppliesModal(false)}
        onSave={(option, supplies) => {
          handleSaveSupplies(option, supplies);
          // Navigate to the next page after selection
          router.push('/booking/datetime');
        }}
      />
    </div>
  );
