  "src/app/booking/summary/page.tsx": "'use client';

import { useState, useEffect } from 'react';
import { useRouter } from 'next/navigation';
import { useAuth } from '@/contexts/AuthContext';
import { bookingAPI } from '@/services/api';
import LoadingOverlay from '@/components/ui/LoadingOverlay';
import FancyLoader from '@/components/ui/FancyLoader';
import { ArrowLeft, MapPin, Home, Calendar, Clock, CheckSquare, Package, Info, DollarSign, ChevronRight } from 'lucide-react';
import Link from 'next/link';
import useBookingStore from '@/store/bookingStore';

export default function BookingSummaryPage() {
  const [loading, setLoading] = useState(true);
  const [processingBooking, setProcessingBooking] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const { isAuthenticated, isLoading } = useAuth();
  const router = useRouter();

  // Booking data state
  const [address, setAddress] = useState<any>(null);
  const [service, setServiceData] = useState<any>(null);
  const [dateTime, setDateTime] = useState<any>(null);
  const [cleaningSupplies, setCleaningSupplies] = useState<{
    option: string;
    supplies: string[];
    discount: number;
  }>({
    option: 'bring-everything',
    supplies: [],
    discount: 0
  });
  const [messiness, setMessiness] = useState(3);
  const [totalPrice, setTotalPrice] = useState(0);
  const [basePrice, setBasePrice] = useState(0);
  const [pets, setPets] = useState<string[]>([]);
  const [frequency, setFrequency] = useState('one-time');
  const [cleaningTime, setCleaningTime] = useState("2 hours 36 min");

  // Get booking store data
  const bookingState = useBookingStore();
  const createBooking = useBookingStore(state => state.createBooking);

  useEffect(() => {
    if (!isLoading && !isAuthenticated) {
      router.push('/auth/login');
      return;
    }

    // Load all the booking data from localStorage and store
    const loadBookingData = () => {
      try {
        // Load address
        const addressData = localStorage.getItem('bookingAddress');
        if (addressData) {
          setAddress(JSON.parse(addressData));
        } else {
          router.push('/booking/address');
          return;
        }

        // Load service
        const serviceData = localStorage.getItem('bookingService');
        if (serviceData) {
          setServiceData(JSON.parse(serviceData));
          setBasePrice(JSON.parse(serviceData).basePrice || 0);
        } else {
          router.push('/booking/services');
          return;
        }

        // Load date/time
        const dateTimeData = localStorage.getItem('bookingDateTime');
        if (dateTimeData) {
          setDateTime(JSON.parse(dateTimeData));
        } else {
          router.push('/booking/datetime');
          return;
        }

        // Get other details from the store or defaults
        // Get supplies info
        let suppliesOption = 'bring-everything';
        let selectedSupplies: string[] = [];
        let suppliesDiscount = 0;

        // Try to get from store directly or via any method
        if (bookingState.currentBooking) {
          // For messiness
          if ('messiness' in bookingState.currentBooking) {
            setMessiness(bookingState.currentBooking.messiness || 3);
          }

          // For pets
          if ('hasPets' in bookingState.currentBooking && bookingState.currentBooking.hasPets) {
            setPets(['has-pets']);
          }

          // For frequency
          if ('frequency' in bookingState.currentBooking) {
            setFrequency(bookingState.currentBooking.frequency || 'one-time');
          }

          // Try to get supplies info from stored details
          if (bookingState.currentBooking.notes) {
            const notes = bookingState.currentBooking.notes;
            if (notes.includes('supplies:')) {
              const suppliesInfo = notes.split('supplies:')[1].split(';')[0].trim();
              if (suppliesInfo.includes('customer-provides-all')) {
                suppliesOption = 'i-have-all';
                suppliesDiscount = 50;
              } else if (suppliesInfo.includes('customer-provides-vacuum')) {
                suppliesOption = 'i-have-vacuum';
                suppliesDiscount = 20;
              }
            }
          }
        }

        setCleaningSupplies({
          option: suppliesOption,
          supplies: selectedSupplies,
          discount: suppliesDiscount
        });

        // Get cleaning time
        const storedCleaningTime = localStorage.getItem('cleaningTime');
        if (storedCleaningTime) {
          setCleaningTime(storedCleaningTime);
        }

        // Calculate total price
        let total = (basePrice || 0) - suppliesDiscount;
        setTotalPrice(Math.max(0, total));
      } catch (error) {
        console.error('Error loading booking data:', error);
        setError('Failed to load booking data');
      } finally {
        setLoading(false);
      }
    };

    if (isAuthenticated) {
      loadBookingData();
    }
  }, [isAuthenticated, isLoading, router, bookingState, basePrice]);

  const handleBookNow = async () => {
    setProcessingBooking(true);
    setError(null);

    try {
      // Prepare any additional booking details in a format the API expects
      // For example, encode supplies info in notes field
      let notesWithSupplies = '';
      if (bookingState.currentBooking && bookingState.currentBooking.notes) {
        notesWithSupplies = bookingState.currentBooking.notes;
      }

      if (cleaningSupplies.option !== 'bring-everything') {
        notesWithSupplies += `; supplies: ${cleaningSupplies.option === 'i-have-all' ? 'customer-provides-all' : 'customer-provides-vacuum'}`;
      }

      // Temporarily add the notes
      if (notesWithSupplies) {
        bookingState.setDetails({
          notes: notesWithSupplies
        });
      }

      const success = await createBooking();
      if (success) {
        // Clear the booking data from localStorage
        localStorage.removeItem('bookingService');
        localStorage.removeItem('bookingAddress');
        localStorage.removeItem('bookingDateTime');
        localStorage.removeItem('cleaningTime');

        // Navigate to bookings page with success message
        router.push('/bookings?success=true');
      } else {
        setError('Failed to create booking. Please try again.');
      }
    } catch (error) {
      console.error('Error creating booking:', error);
      setError('Failed to create booking. Please try again.');
    } finally {
      setProcessingBooking(false);
    }
  };

  if (isLoading || loading) {
    return <FancyLoader visible={true} message="Loading booking details..." />;
  }

  if (processingBooking) {
    return <FancyLoader visible={true} message="Creating your booking..." />;
  }

  // Format preferred dates for display
  const formatDate = (dateString: string) => {
    const date = new Date(dateString);
    const days = ['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'];
    const months = ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec'];

    return `${days[date.getDay()]}, ${months[date.getMonth()]} ${date.getDate()}`;
  };

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="max-w-md mx-auto bg-white">
        {/* Header */}
        <div className="py-4 flex items-center border-b border-gray-200 px-4">
          <Link href="/booking/datetime" className="mr-2">
            <ArrowLeft size={20} className="text-gray-800" />
          </Link>
          <h1 className="text-xl font-semibold">Review & Confirm</h1>
        </div>

        <div className="px-4 py-6">
          <h2 className="text-2xl font-bold text-gray-800 mb-1">Review your booking</h2>
          <p className="text-gray-500 mb-6">
            Please confirm the details of your cleaning service
          </p>

          {error && (
            <div className="mb-4 bg-red-50 border border-red-200 text-red-600 p-3 rounded-lg">
              {error}
            </div>
          )}

          {/* Location Section */}
          <div className="bg-white rounded-lg p-4 mb-4 shadow-sm">
            <div className="flex items-center mb-3">
              <MapPin className="text-blue-500 mr-2" size={20} />
              <h2 className="text-lg font-semibold text-gray-800">Location</h2>
            </div>

            {address && (
              <Link href="/booking/address" className="block">
                <div className="cursor-pointer">
                  <h3 className="font-medium text-gray-800">{address.name}</h3>
                  <p className="text-gray-600">{address.streetAddress}, {address.city}, {address.state} {address.zipCode}</p>
                  <p className="text-gray-600">{address.squareFootage} sq ft</p>
                  {address.notes && (
                    <p className="text-gray-500 italic mt-1">Note: {address.notes}</p>
                  )}
                </div>
              </Link>
            )}
          </div>

          {/* Property Details Section */}
          <div className="bg-white rounded-lg p-4 mb-4 shadow-sm">
            <div className="flex items-center mb-3">
              <Home className="text-blue-500 mr-2" size={20} />
              <h2 className="text-lg font-semibold text-gray-800">Property Details</h2>
            </div>

            <div className="flex justify-between items-center mb-2">
