'use client';

import { useState, useEffect } from 'react';
import { useRouter, useParams } from 'next/navigation';
import { addressAPI } from '@/services/api';
import { ArrowLeft } from 'lucide-react';
import Link from 'next/link';
import LoadingOverlay from '@/components/ui/LoadingOverlay';

export default function EditAddressPage() {
  const params = useParams();
  const addressId = params.id as string;
  const router = useRouter();
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [formData, setFormData] = useState({
    name: '',
    streetAddress: '',
    city: '',
    state: '',
    zipCode: '',
    squareFootage: 0,
    notes: '',
    isDefault: false
  });

  useEffect(() => {
    const fetchAddress = async () => {
      try {
        setLoading(true);
        const addresses = await addressAPI.getUserAddresses();
        const address = addresses.find((a: any) => a._id === addressId);

        if (!address) {
          setError('Address not found');
          return;
        }

        setFormData({
          name: address.name,
          streetAddress: address.streetAddress,
          city: address.city,
          state: address.state,
          zipCode: address.zipCode,
          squareFootage: address.squareFootage,
          notes: address.notes || '',
          isDefault: address.isDefault || false
        });
      } catch (err) {
        console.error('Error fetching address:', err);
        setError('Failed to load address details');
      } finally {
        setLoading(false);
      }
    };

    fetchAddress();
  }, [addressId]);

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
    const { name, value, type } = e.target as HTMLInputElement;

    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' 
        ? (e.target as HTMLInputElement).checked 
        : name === 'squareFootage' 
          ? parseInt(value) || 0 
          : value
    }));
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setError(null);

    try {
      setLoading(true);
      await addressAPI.updateAddress(addressId, formData);
      router.push('/booking/address');
    } catch (err) {
      console.error('Error updating address:', err);
      setError('Failed to update address');
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return <LoadingOverlay visible={true} message="Loading address..." />;
  }

  return (
    <div className="min-h-screen bg-white pb-20">
      {/* Header */}
      <div className="border-b border-gray-200">
        <div className="px-4 py-4 flex items-center">
          <Link href="/booking/address" className="mr-2">
