            />

            <Input 
              type="text"
              label="Last Name"
              placeholder="Enter last name"
              value={lastName}
              onChange={(e) => setLastName(e.target.value)}
              leftIcon={<User size={20} className="text-textLight" />}
              required
            />
          </div>

          <Input 
            type="email"
            label="Email"
            placeholder="Enter your email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            leftIcon={<Mail size={20} className="text-textLight" />}
            required
          />

          <Input 
            type="tel"
            label="Phone (optional)"
            placeholder="Enter your phone number"
            value={phone}
