  "src/contexts/AuthContext.tsx": "// src/contexts/AuthContext.tsx
'use client';

import React, { createContext, useContext, useState, useEffect, ReactNode } from 'react';
import { authAPI } from '@/services/api';

interface User {
  _id: string;
  firstName: string;
  lastName: string;
  email: string;
  phone?: string;
  role?: 'customer' | 'cleaner' | 'admin';
}

interface AuthContextType {
  user: User | null;
  isLoading: boolean;
  isAuthenticated: boolean;
  login: (email: string, password: string) => Promise<void>;
  register: (userData: any) => Promise<void>;
  logout: () => void;
  error: string | null;
}

const AuthContext = createContext<AuthContextType | undefined>(undefined);

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (context === undefined) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};

interface AuthProviderProps {
  children: ReactNode;
}

export const AuthProvider = ({ children }: AuthProviderProps) => {
  const [user, setUser] = useState<User | null>(null);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  // Check if user is already logged in on initial load
  useEffect(() => {
    const checkAuth = async () => {
      //const token = localStorage.getItem('token');
        try {
          const userData = await authAPI.getUserProfile();
          setUser(userData.user);
        } catch (err) {
          // Token might be expired or invalid
          localStorage.removeItem('token');
        } finally {
          setIsLoading(false);
        }
      };

    checkAuth();
  }, []);

  const login = async (email: string, password: string) => {
    setIsLoading(true);
    setError(null);
    try {
      const response = await authAPI.login(email, password);
      //localStorage.setItem('token', response.token);
      setUser({
        _id: response._id,
        firstName: response.firstName,
        lastName: response.lastName,
        email: response.email,
        phone: response.phone,
        role: response.role,
      });
    } catch (err: any) {
      setError(err.response?.data?.message || 'Failed to login');
      throw err;
    } finally {
      setIsLoading(false);
    }
  };

  const register = async (userData: any) => {
    setIsLoading(true);
    setError(null);
    try {
      const response = await authAPI.register(userData);
      //localStorage.setItem('token', response.token);
      setUser({
        _id: response._id,
        firstName: response.firstName,
        lastName: response.lastName,
        email: response.email,
        phone: response.phone,
        role: response.role,
      });
    } catch (err: any) {
      setError(err.response?.data?.message || 'Failed to register');
      throw err;
    } finally {
      setIsLoading(false);
    }
  };

  const logout = async () => {
    try {
      // Call the logout endpoint to clear cookies on the server
      await authAPI.logout();
    } catch (err) {
      console.error("Error during logout:", err);
    } finally {
      // Clear any legacy tokens
      localStorage.removeItem('token');
      setUser(null);
    }
  };

  return (
    <AuthContext.Provider
      value={{
        user,
        isLoading,
        isAuthenticated: !!user,
        login,
        register,
        logout,
        error,
      }}
    >
      {children}
    </AuthContext.Provider>
  );
};

export default AuthProvider;",
  "src/middleware.ts": "// Update your src/middleware.ts
import { NextResponse } from 'next/server';
import type { NextRequest } from 'next/server';

export function middleware(request: NextRequest) {
  // Check for token in both cookies and local storage
  const token = request.cookies.get('token') || 
                // Can't access localStorage in middleware, so we just check cookies
                null;

  // Define auth routes
  const authRoutes = ['/login', '/register'];

  // Check if current path is an auth route
  const isAuthRoute = authRoutes.some(route => 
    request.nextUrl.pathname === route || 
    request.nextUrl.pathname.startsWith(route)
  );

  // Debug info in headers (you can remove this in production)
  const response = NextResponse.next();
  response.headers.set('x-middleware-cache', 'no-cache');

  // Redirect to login if no token and trying to access non-auth pages
  if (!token && request.nextUrl.pathname === '/') {
    return NextResponse.redirect(new URL('/login', request.url));
  }

  // Redirect to home if has token and trying to access auth routes
  if (token && isAuthRoute) {
    return NextResponse.redirect(new URL('/', request.url));
  }

  return response;
}

// Run middleware on specified routes
export const config = {
  matcher: ['/', '/login', '/register'],
};",
