// src/app/bookings/page.tsx
'use client';

import { useState, useEffect } from 'react';
import { useRouter } from 'next/navigation';
import { useAuth } from '@/contexts/AuthContext';
import { bookingAPI } from '@/services/api';
import LoadingOverlay from '@/components/ui/LoadingOverlay';
import Button from '@/components/ui/Button';
import Card from '@/components/ui/Card';
import { Calendar, Clock, MapPin, Filter } from 'lucide-react';
import FancyLoader from '@/components/ui/FancyLoader';

export default function BookingsPage() {
  const [bookings, setBookings] = useState<any[]>([]);
  const [filteredBookings, setFilteredBookings] = useState<any[]>([]);
  const [activeFilter, setActiveFilter] = useState<'upcoming' | 'past'>('upcoming');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const { isAuthenticated, isLoading } = useAuth();
  const router = useRouter();

  useEffect(() => {
    if (!isLoading && !isAuthenticated) {
      router.push('/login');
      return;
    }

    const fetchBookings = async () => {
      try {
        const bookingsData = await bookingAPI.getUserBookings();
        setBookings(bookingsData);
        filterBookings(bookingsData, activeFilter);
      } catch (err: any) {
        setError('Failed to load bookings');
        console.error('Error fetching bookings:', err);
      } finally {
        setLoading(false);
      }
    };

    if (isAuthenticated) {
      fetchBookings();
    }
  }, [isAuthenticated, isLoading, router, activeFilter]);

  const filterBookings = (bookings: any[], filter: 'upcoming' | 'past') => {
    const now = new Date();

    if (filter === 'upcoming') {
      const upcoming = bookings.filter(booking => {
        const bookingDate = new Date(booking.date);
        return bookingDate >= now || (booking.status !== 'completed' && booking.status !== 'cancelled');
      });
      setFilteredBookings(upcoming);
    } else {
      const past = bookings.filter(booking => {
        const bookingDate = new Date(booking.date);
        return bookingDate < now || booking.status === 'completed' || booking.status === 'cancelled';
      });
      setFilteredBookings(past);
    }
  };

  const handleFilterChange = (filter: 'upcoming' | 'past') => {
    setActiveFilter(filter);
    filterBookings(bookings, filter);
  };

  // Format date helper function
  const formatDate = (dateString: string): string => {
    const date = new Date(dateString);
    return date.toLocaleDateString('en-US', {
      weekday: 'short',
      month: 'short', 
      day: 'numeric'
    });
  };

  // Get status color
  const getStatusColor = (status: string): string => {
    switch (status.toLowerCase()) {
      case 'confirmed':
        return 'bg-primary text-white';
      case 'pending':
        return 'bg-warning text-white';
      case 'completed':
        return 'bg-success text-white';
      case 'cancelled':
        return 'bg-error text-white';
      default:
        return 'bg-grayLight text-text';
    }
  };

  if (isLoading || loading) {
    return <FancyLoader visible={true} message="Loading bookings..." />;
  }

  return (
    <div className="min-h-screen bg-background px-4 py-6 sm:px-6 lg:px-8">
      <div className="max-w-3xl mx-auto">
        <div className="mb-6">
          <h1 className="text-2xl font-bold text-text mb-2">Bookings</h1>
          <p className="text-textLight">View and manage your cleaning services</p>
        </div>

        <div className="mb-6 flex justify-between">
          <div className="flex space-x-2">
            <Button
              title="Upcoming"
              variant={activeFilter === 'upcoming' ? 'primary' : 'outline'}
              size="small"
              onClick={() => handleFilterChange('upcoming')}
            />

            <Button
              title="Past"
              variant={activeFilter === 'past' ? 'primary' : 'outline'}
              size="small"
              onClick={() => handleFilterChange('past')}
            />
          </div>

          <Button
            title="Book New"
            variant="primary"
            icon={<Calendar size={16} />}
            iconPosition="left"
            size="small"
            onClick={() => router.push('/booking/new')}
          />
        </div>

        {filteredBookings.length > 0 ? (
          <div className="space-y-4">
            {filteredBookings.map(booking => (
              <Card 
                key={booking._id} 
                className="cursor-pointer hover:shadow-md transition-shadow"
                onClick={() => router.push(`/bookings/${booking._id}`)}
              >
                <div className="p-4">
                  <div className="flex justify-between items-start mb-3">
                    <div>
                      <h3 className="font-semibold text-text">{booking.serviceType?.name || 'Cleaning Service'}</h3>
                      <div className="text-sm text-textLight">
                        ${booking.price}  {booking.squareFootage} sq ft
                      </div>
                    </div>
                    <div className={`px-2 py-1 rounded-full text-xs font-medium ${getStatusColor(booking.status)}`}>
                      {booking.status.charAt(0).toUpperCase() + booking.status.slice(1)}
                    </div>
                  </div>

                  <div className="space-y-2">
                    <div className="flex items-center">
                      <Calendar size={16} className="text-primary mr-2 flex-shrink-0" />
                      <span className="text-sm text-text">{formatDate(booking.date)}</span>
                    </div>

                    <div className="flex items-center">
                      <Clock size={16} className="text-primary mr-2 flex-shrink-0" />
                      <span className="text-sm text-text">{booking.timeSlot}</span>
                    </div>

                    <div className="flex items-center">
                      <MapPin size={16} className="text-primary mr-2 flex-shrink-0" />
                      <span className="text-sm text-text truncate">
                        {booking.address?.streetAddress || 'Address not available'}
                      </span>
                    </div>
                  </div>
                </div>
              </Card>
            ))}
          </div>
        ) : (
          <Card className="p-6 text-center">
            <p className="text-textLight mb-4">
              {activeFilter === 'upcoming' 
                ? "You don't have any upcoming bookings."
                : "You don't have any past bookings."
              }
            </p>
            {activeFilter === 'upcoming' && (
              <Button
                title="Book a Cleaning"
                variant="primary"
                onClick={() => router.push('/booking/new')}
              />
            )}
          </Card>
        )}
      </div>
    </div>
  );
}

