          <p className="text-textLight">Sign up to get started</p>
        </div>

        <form onSubmit={handleSubmit}>
          <div className="grid grid-cols-2 gap-4">
            <Input 
              type="text"
              label="First Name"
              placeholder="Enter first name"
              value={firstName}
              onChange={(e) => setFirstName(e.target.value)}
              leftIcon={<User size={20} className="text-textLight" />}
              required
            />

            <Input 
              type="text"
              label="Last Name"
              placeholder="Enter last name"
              value={lastName}
              onChange={(e) => setLastName(e.target.value)}
              leftIcon={<User size={20} className="text-textLight" />}
              required
            />
          </div>

          <Input 
            type="email"
            label="Email"
            placeholder="Enter your email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            leftIcon={<Mail size={20} className="text-textLight" />}
            required
          />

          <Input 
            type="tel"
            label="Phone (optional)"
            placeholder="Enter your phone number"
            value={phone}
            onChange={(e) => setPhone(e.target.value)}
            leftIcon={<Phone size={20} className="text-textLight" />}
          />

          <Input 
            type="password"
            label="Password"
            placeholder="Create a password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            leftIcon={<Lock size={20} className="text-textLight" />}
            required
          />

          <Input 
            type="password"
            label="Confirm Password"
            placeholder="Confirm your password"
            value={confirmPassword}
            onChange={(e) => setConfirmPassword(e.target.value)}
            leftIcon={<Lock size={20} className="text-textLight" />}
            required
          />

          <div className="mb-4">
            <label className="block text-sm font-medium text-text mb-2">I am a:</label>
            <div className="flex space-x-4">
              <div 
                className={`flex-1 p-3 border rounded-lg cursor-pointer transition-colors ${
                  role === 'customer' 
                    ? 'border-primary bg-primary bg-opacity-5' 
                    : 'border-border'
                }`}
