const HomePage: React.FC = () => {
  // Define the type for activeService or use `Service | null`
  const [activeService, setActiveService] = useState<Service | null>(null);
  const [autoScrollEnabled, setAutoScrollEnabled] = useState(true);

  // Type the ref so its a div or null
  const carouselRef = useRef<HTMLDivElement | null>(null);

  // Use ReturnType<typeof setTimeout> so TS knows its a browser timer
  const autoScrollTimerRef = useRef<ReturnType<typeof setTimeout> | null>(null);

  const services: Service[] = [
    {
      id: 'deep-clean',
      name: 'Deep Clean',
      description: 'A comprehensive cleaning for spaces that need extra attention.',
      icon: <Sparkles size={32} />,
      color: 'bg-blue-500',
      textColor: 'text-white',
      basePrice: 200
    },
    {
      id: 'move-in-out',
      name: 'Move In/Out',
      description: 'Prepare your space for moving in or out with a complete cleaning.',
      icon: <Box size={32} />,
      color: 'bg-purple-500',
      textColor: 'text-white',
      basePrice: 250
    },
    {
      id: 'party-clean',
      name: 'Party Clean',
      description: 'Quick cleanup after events and gatherings.',
      icon: <PartyPopper size={32} />,
      color: 'bg-pink-500',
      textColor: 'text-white',
      basePrice: 150
    },
    {
      id: 'standard-clean',
      name: 'Standard Clean',
      description: 'A thorough cleaning of your space, focusing on regular maintenance.',
      icon: <Home size={32} />,
      color: 'bg-green-500',
      textColor: 'text-white',
      basePrice: 120
    }
  ];

  // Function to handle service selection
  const handleServiceSelect = (service: Service) => {
    setActiveService(service);
    setAutoScrollEnabled(false);

    // Store the selected service in cookies
    Cookies.set('bookingService', JSON.stringify({
      serviceId: service.id,
      serviceName: service.name,
      serviceDescription: service.description,
      basePrice: service.basePrice
    }));
  };

  // Auto-scrolling functionality
  useEffect(() => {
    if (!autoScrollEnabled || !carouselRef.current) return;

    const scrollRight = () => {
      // Always check .current
      if (!carouselRef.current) return;

      const { scrollLeft, scrollWidth, clientWidth } = carouselRef.current;
      const newScrollLeft = scrollLeft + 300;

      // If we're at the end, scroll back to the beginning
      if (scrollLeft + clientWidth >= scrollWidth - 10) {
        carouselRef.current.scrollTo({ left: 0, behavior: 'smooth' });
      } else {
        // Otherwise, continue scrolling right
        carouselRef.current.scrollTo({ left: newScrollLeft, behavior: 'smooth' });
      }
    };

    // Set up the auto-scroll timer
    autoScrollTimerRef.current = setInterval(scrollRight, 3000);

    return () => {
      // Clear the interval on unmount
      if (autoScrollTimerRef.current) {
        clearInterval(autoScrollTimerRef.current);
      }
    };
  }, [autoScrollEnabled]);

  // Handle manual scrolling - pause auto-scroll when user interacts
  const handleManualScroll = () => {
    setAutoScrollEnabled(false);

    // Clear the old timer if it exists
    if (autoScrollTimerRef.current) {
      clearTimeout(autoScrollTimerRef.current);
    }

    // Resume auto-scroll after 8 seconds of inactivity
    autoScrollTimerRef.current = setTimeout(() => {
      setAutoScrollEnabled(true);
    }, 8000);
  };

  // Scroll control functions
  const scrollLeft = () => {
    if (!carouselRef.current) return;
    const newScrollLeft = carouselRef.current.scrollLeft - 300;
    carouselRef.current.scrollTo({ left: newScrollLeft, behavior: 'smooth' });
    handleManualScroll();
  };

  const scrollRight = () => {
    if (!carouselRef.current) return;
    const newScrollLeft = carouselRef.current.scrollLeft + 300;
    carouselRef.current.scrollTo({ left: newScrollLeft, behavior: 'smooth' });
    handleManualScroll();
  };

  return (
    <div className="min-h-screen bg-white">
