              style={{ scrollbarWidth: 'none', msOverflowStyle: 'none' }}
              onScroll={handleManualScroll}
            >
              {services.map((service) => (
                <div
                  key={service.id}
                  className={`snap-center flex-shrink-0 w-72 h-48 rounded-xl shadow-lg overflow-hidden cursor-pointer transition-transform transform hover:scale-105 ${
                    activeService?.id === service.id ? 'ring-4 ring-blue-400' : ''
