// Update your src/middleware.ts
import { NextResponse } from 'next/server';
import type { NextRequest } from 'next/server';

export function middleware(request: NextRequest) {
  // Check for token in both cookies and local storage
  const token = request.cookies.get('token') || 
                // Can't access localStorage in middleware, so we just check cookies
                null;

  // Define auth routes
  const authRoutes = ['/login', '/register'];

  // Check if current path is an auth route
  const isAuthRoute = authRoutes.some(route => 
    request.nextUrl.pathname === route || 
    request.nextUrl.pathname.startsWith(route)
  );

  // Debug info in headers (you can remove this in production)
  const response = NextResponse.next();
  response.headers.set('x-middleware-cache', 'no-cache');

  // Redirect to login if no token and trying to access non-auth pages
  if (!token && request.nextUrl.pathname === '/') {
    return NextResponse.redirect(new URL('/login', request.url));
  }

  // Redirect to home if has token and trying to access auth routes
  if (token && isAuthRoute) {
    return NextResponse.redirect(new URL('/', request.url));
  }

  return response;
}

// Run middleware on specified routes
export const config = {
  matcher: ['/', '/login', '/register'],
};

